void solve() {
    int n = 24;
    int l = 0, r = 100, ans = n;
    while(l &lt;= r) {
        int mid = (l + r) / 2;
        if(mid * mid &lt;= n) {
            ans = mid;
            l = mid + 1;
        }
        else {
            r = mid - 1;
        }
    }
    printf("%d", ans);
}
